# Parse the data
def parse_foreclosures(text):
    records = []

    # Remove page headers and footers
    text = re.sub(r'© 2026 Bexar County.*?Property Address', '', text, flags=re.DOTALL)
    text = re.sub(r'Powered By GovOS.*?Property Address', '', text, flags=re.DOTALL)

    # Pattern to match foreclosure entries
    # NOTICE OF FORECLOSE <date> <date> <number> <remarks> <address>, <city>, <state>, <zip>
    pattern = r'NOTICE OF FORECLOSE\s+(\d{1,2}/\d{1,2}/\d{4})\s+(\d{2}/\d{2}/\d{4})\s+(\d+)\s+(.*?),\s*([A-Z\s]+),\s*TEXAS,\s*(\d{5}(?:-\d{4})?)'

    matches = re.finditer(pattern, text)

    for match in matches:
        recorded_date = match.group(1)
        sale_date = match.group(2)
        doc_number = match.group(3)
        remarks_and_address = match.group(4)
        city = match.group(5).strip()
        zip_code = match.group(6)

        # Split remarks and address - find the last comma before city
        # Remarks contain location info, address is the street address
        parts = remarks_and_address.rsplit(',', 1)
        if len(parts) == 2:
            remarks = parts[0].strip()
            street_address = parts[1].strip()
        else:
            # If we can't split properly, treat it all as remarks
            remarks = remarks_and_address.strip()
            street_address = ""

        full_address = f"{street_address}, {city}, TEXAS, {zip_code}".strip(", ")

        records.append({
            'Doc Type': 'NOTICE OF FORECLOSE',
            'Recorded Date': recorded_date,
            'Sale Date': sale_date,
            'Doc Number': doc_number,
            'Remarks': remarks,
            'Street Address': street_address,
            'City': city,
            'State': 'TEXAS',
            'Zip Code': zip_code,
            'Full Address': full_address
        })

    return records

# Parse the data
records = parse_foreclosures(raw_data)

# Write to CSV
csv_file = 'foreclosures.csv'
if records:
    with open(csv_file, 'w', newline='', encoding='utf-8') as f:
        writer = csv.DictWriter(f, fieldnames=[
            'Doc Type', 'Recorded Date', 'Sale Date', 'Doc Number',
            'Remarks', 'Street Address', 'City', 'State', 'Zip Code', 'Full Address'
        ])
        writer.writeheader()
        writer.writerows(records)

    print(f"Successfully parsed {len(records)} foreclosure records to {csv_file}")
    print(f"\nFirst few records:")
    for i, record in enumerate(records[:3], 1):
        print(f"\n{i}. {record['Doc Number']}")
        print(f"   Address: {record['Full Address']}")
        print(f"   Recorded: {record['Recorded Date']}, Sale: {record['Sale Date']}")
else:
    print("No records found!")